mapper.update:
UPDATE License       
SET l_group = #{lGroup},      
    l_grade = #{lGrade},      
    l_num = #{lNum},        
    l_date = TO_DATE(#{lDate},'YYYY/MM/DD'),       
    l_organ = #{lOrgan}      
WHERE  member_id = #{memberId} 
AND l_name=#{lName}

mapper.select:
SELECT
    
FROM license
WHERE member_id = #{memberId}
AND l_name=#{lName}

mapper.delete:
   DELETE FROM License
   WHERE member_id=#{memberId} 
   AND l_name=#{lName}
